import React from "react";
import { useForm } from "../../hooks/useForm";

const initialForm = {};

const validationsForm = (form) => {};

const ContactForm = () => {
  const {
    form,
    errors,
    loading,
    response,
    handleChange,
    handleBlur,
    handleSubmit,
  } = useForm(initialForm, validationsForm);

  return (
    <div className="container">
      <div className="col-md-8">
        <h3 className="title-form">
          Formulario de Solicitud de Homologación de Equipos Terminales Móviles
        </h3>
        <br/><br/>
        <form className="row" onSubmit={handleSubmit}>


          <input
            type="text"
            name="name"
            placeholder="Ej: Pedro"
            onBlur={handleBlur}
            onChange={handleChange}
            value={form.name}
            required
          />
          <input
            type="text"
            name="lastname"
            placeholder="Ej: Rodriguez"
            onBlur={handleBlur}
            onChange={handleChange}
            value={form.lastname}
            required
          />
          <input
            type="email"
            name="email"
            placeholder="Ej: pedro.rodriguez@gmail.com"
            onBlur={handleBlur}
            onChange={handleChange}
            value={form.email}
            required
          />
          <input
            type="text"
            name="subject"
            placeholder="Ej: El presenta asunto para tratar..."
            onBlur={handleBlur}
            onChange={handleChange}
            value={form.subject}
            required
          />
          <input
            name="comments"
            cols="50"
            rows="5"
            placeholder="Ej: Escribe tus comentarios..."
            onBlur={handleBlur}
            onChange={handleChange}
            value={form.comments}
            required
          />
          <input type="submit" value="Enviar" />
        </form>
      </div>
    </div>
  );
};

export default ContactForm;
