  const [loading, setLoaging] = useState(false);
  const [response, setResponse] = useState(null);

  const handle = (e) => {}

  const handleBlur = (e) => {}

  const handleSubmit = (e) => {}

  return {
      form, 
      errors, 
      loading, 
      response, 
      handleChange, 
      handleBlur, 
      handleSubmit
  }
};
