import React, { useRef, useState } from "react";
import "./Upload.scss";

export default function Dragdrop() {
  const [files, setFiles] = useState(null);
  const inputRef = useRef();

  const handleClick = () => {
    inputRef.current.click();
  };

  const removeFile = (name) => {
    const newFiles = files.filter((file) => file.name !== name);
    setFiles(newFiles);
  };

  const preventBubbling = (e) => {
    e.stopPropagation();
    e.preventDefault();
  };

  return (
    <div className="Upload" css={CSS}>
      <div className="inner">
        <div className="list">
          <h5>Your Files:</h5>

          {files && (

            <>

            <table className="rwd-table">
          <tr>
            <th>Nombre del Archivo</th>
            <th>Archivo Cargado	</th>
            <th>Eliminar Archivo</th>
          </tr>
          <tr>
            <td data-th="etiqueta">Etiqueta del equipo *</td>
            <td data-th="archivo">{file.name}</td>
            <td data-tj="accion"><span onClick={() => removeFile(file.name)}>
                    <i className="fa fa-times">ELIMINAR</i>
                  </span></td>
          </tr>
          <tr>
            <td data-th="etiqueta">Certificado de conformidad de normas técnicas (opcional)</td>
            <td data-th="archivo"></td>
            <td data-tj="accion"></td>
          </tr>
        </table>






            </>
          )}
        </div>

        <div
          className="form"
          onDragEnter={preventBubbling}
          onDragOver={preventBubbling}
          onDrop={(e) => {
            preventBubbling(e);
            setFiles(Array.from(e.dataTransfer.files));
          }}
        >
          <i className="fa fa-cloud-upload fa-4x"></i>
          <p>Arrastre aquí su archivo o haga click para añadir.</p>

          <input
            ref={inputRef}
            type="file"
            multiple
            style={{ display: "none" }}
            onChange={(e) => setFiles(Array.from(e.target.files))}
          />
          <button onClick={handleClick}>Choose Files</button>
        </div>
      </div>
    </div>
  );
}
