import React from 'react'
import { Input, Label, GrupoInput, LeyendaError } from "../elementos/Formularios"

export default function ComponenteInput(
    {
        estado, 
        cambiarEstado, 
        tipo, 
        label, 
        placeholder, 
        name, 
        leyendaError, 
    }
) {



    /*
    const validacion = () => {
		if(expresionRegular){
			if(expresionRegular.test(estado.campo)){
				cambiarEstado({...estado, valido: 'true'});
			} else {
				cambiarEstado({...estado, valido: 'false'});
			}
		}

		if(funcion){
			funcion();
		}
	}
    */

    return (
        <div>
            <Label htmlFor={name}>{label}</Label>
            <GrupoInput>
                <Input 
                    type={tipo} 
                    placeholder={placeholder} 
                    id={name} 
                    value={estado.campo}
                />
            </GrupoInput>
            <LeyendaError>{leyendaError}</LeyendaError>
        </div>
    )
}
