import styled from 'styled-components';

const colores = {
    borde: "#3366CC",
    error: "#A80521",
    exito: "#3366CC"
}

const Formulario = styled.form `
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 20px;

    @media (max-width: 800px) {
        grid-template-columns: 1fr;
    }
`;

const Label = styled.label `
    display: block;
    font: normal 500 16px Work Sans;
    line-height: 1.5;
    margin: 0;
    color: #4B4B4B;
    padding: padding: 22px 0px 0px 0px;
    cursor: pointer;
`;

const GrupoInput = styled.div `
    position: relative;
    z-index: 90;
`;

const Input = styled.input `
    width: 100%;
    background: #fff;
    border-radius: 3px;
    height: 35px;
    line-height: 35px;
    padding: 0 40px 0 10px;
    transition: .3s ease all;
    border: 1px solid #BABABA;

    &:focus {
        border: 1px solid ${colores.borde};
        outline: none;
        box-shadow: 3px 0px 30px rgba(163,163,163, 0.4);
    }
`;

const LeyendaError = styled.p `
    font-size: 12px;
    margin-bottom: 0;
    color: ${colores.error};
`;

const ContenedorTerminos = styled.div `
    grid-column: span 2;

    input {
        margin-right: 10px;
    }
`;

const ContenedorBotonCentrado = styled.div `
    display: flex;
    flex-direction: column;
    align-items: center;
    grid-column: span 2;
`;

const Boton = styled.button `
    height: 40px;
    line-height: 30px;
    width: 30%;
    background: #3366CC;
    color: #fff;
    font: normal bold 16px/5px "Work Sans", sans-serift;
    border: none;
    border-radius: 25px;
    cursor: pointer;
    transition: .1s ease all;

    &:hover {
        background: #004884;
    }
`;

const MensjaeExito = styled.p `
    font-size: 14px;
    color: ${colores.exito};
    /*display: none*/
`;

const MensajeError = styled.div `
    height: 45px;
    line-height: 45px;
    background: ${colores.error};
    padding: 0px 15px;
    border-radius: 3px;
    grid-column: span 2;

`;

export { 
    Formulario, Label, GrupoInput, 
    Input, LeyendaError, ContenedorTerminos,
    ContenedorBotonCentrado, Boton, MensjaeExito,
    MensajeError 
};
