import React from "react";
import { useForm } from "../../hooks/useForm";

const initialForm = {};

const validationsForm = (form) => {};

const ContactForm = () => {
  const {
    form,
    errors,
    loading,
    response,
    handleChange,
    handleBlur,
    handleSubmit,
  } = useForm(initialForm, validationsForm);

  return (
    <div className="container">

        <h3 className="title-form">
          Formulario de Solicitud de Homologación de Equipos Terminales Móviles
        </h3>
        <br/><br/>
        <form onSubmit={handleSubmit}>

            <div className="row col-md-8">

            <div className="col-md-6">
                <input
                    type="text"
                    name="name"
                    placeholder="Ej: Pedro"
                    onBlur={handleBlur}
                    onChange={handleChange}
                    value={form.name}
                    required
                />

            </div>
            <div className="col-md-6">
                <input
                    type="text"
                    name="lastname"
                    placeholder="Ej: Rodriguez"
                    onBlur={handleBlur}
                    onChange={handleChange}
                    value={form.lastname}
                    required
                />    
            </div>
            <div className="col-md-6">
            <input
            type="email"
            name="email"
            placeholder="Ej: pedro.rodriguez@gmail.com"
            onBlur={handleBlur}
            onChange={handleChange}
            value={form.email}
            required
          />
            </div>
            <div className="col-md-6">
            <input
            type="text"
            name="subject"
            placeholder="Ej: El presenta asunto para tratar..."
            onBlur={handleBlur}
            onChange={handleChange}
            value={form.subject}
            required
          />
            </div>
            <div className="col-md-6">

            </div>



          <input
            name="comments"
            cols="50"
            rows="5"
            placeholder="Ej: Escribe tus comentarios..."
            onBlur={handleBlur}
            onChange={handleChange}
            value={form.comments}
            required
          />
          <input type="submit" value="Enviar" />
            </div>

        </form>

    </div>
  );
};

export default ContactForm;
