import React, { useRef, useState } from "react";
import ReCAPTCHA from "react-google-recaptcha";
import { Link } from "react-router-dom";
import Footer from "../../components/Footer/Footer";
import FooterGov from "../../components/FooterGov/FooterGov";
import Header from "../../components/Header/Header";
import Navigation from "../../components/Navigation/Navigation";
import NavProceso from "../../components/NavProceso/NavProceso";

import BotonTutoriales from "../../components/Botones/BotonTutoriales/BotonTutoriales";
import BotonDeDudas from "../../components/Botones/BotonDeDudas/BotonDeDudas";
import BotonInicio from "../../components/Botones/BotonInicio/BotonInicio";

import CalificacionExperiencia from "../../components/Cards/CalificacionExperiencia/CalificacionExperiencia";

import styled from "styled-components"

import "./ConsultaEstadoTramite.scss";

const ConsultaEstadoTramite = () => {

  const [captchaValido, cambiarCaptchaValido] = useState(null);
  const [usuarioValido, cambiarUsuarioValido] = useState(false);

  const captcha = useRef(null);

  const onChange = () => {
    if(captcha.current.getValue()){
      console.log('El usuario no es un robot');
      cambiarCaptchaValido(true);
    }
  }

  const submit = (e) => {
    e.preventDefault();

    //Colocar aquí la validación de los inputs del formulario
    //Si son correctos ya podemos enviar el formulario, actualizar la interfaz etc

    //console.log("Comprobando envio del formulario")
    if(captcha.current.getValue()){
      console.log('El usuario no es un robot');
      cambiarUsuarioValido(true);
      cambiarCaptchaValido(true);
    } else {
      console.log('Por favor acepta el captcha');
      cambiarUsuarioValido(false);
      cambiarCaptchaValido(false);
    }
  }

  return (
    <>
      <div className="container">
        <Header />
        <div className="row">
        {!usuarioValido && 
          <div className="col-md-8">
            <Navigation />
            <NavProceso /> <br />
            <h3 className="title-form-radicado">
              Consultar Mis Trámites
            </h3>
            <br/>
            <p>
             A través de la siguiente funcionalidad, consulte el estado de sus solicitudes presentadas 
             ante la Comisión de Regulación de Comunicaciones. La búsqueda la puede realizar diligenciando 
             los campos dispuestos continuación:
            </p>
            <br />
            <br />

            <form className="row" action="" onSubmit={submit}>
              <div className="col-md-12 form-radicado">
                <label>
                  <p className="txt-form">
                    Número de Radicado *
                  </p>
                </label>
                <input
                  name="radicao"
                  placeholder="Ej: 123456789"
                  className="form-control"
                  type="text"
                >
                </input>
                <div className="recaptcha">
                  <ReCAPTCHA
                    ref={captcha}
                    sitekey="6Lc9VDIeAAAAAHHQA1wEjx1FKlTy9uWIrZKGwwvN"
                    onChange={onChange}
                  />
                </div>
              </div>
              {
                captchaValido === false && 
                <div className="error-captcha">Por favor acepta el captcha</div>
              }

              <Boton type="submit">CONSULTAR</Boton>
            </form>
          </div>
        }
        {
          usuarioValido &&
            <div>
              <h1>BIENVENIDO A VER EL PROCESO COMO VA</h1>
            </div>
        }
          <div className="col-lg-1"></div>
          <div className="col-lg-3 p-0">
            <aside className="aside">
              <br />
              <br />
              <BotonTutoriales />
              <BotonDeDudas />
              <br />
              <br />
              <CalificacionExperiencia />
              <br />
              <br />
              <BotonInicio />
            </aside>
          </div>
        </div>
      </div>
      <Footer />
      <FooterGov />
    </>
  );
};

export default ConsultaEstadoTramite;


const Boton = styled.button`
  border-radius: 20px;
  border_radius: 100px;
  border: none;
  background: #1766DC;
  color: #ffff;
  cursor: pointer;
  display: block;
  font-family: 'Roboto', sans-serif;
  font-weight: 500;
  padding: 10px 30px;
  transition: .3s ease all;

  &:hover {
    background: #0066FF;
  }
`
