import React, {useState} from "react";

import {
  Formulario,
  Label,
  ContenedorTerminos,
  ContenedorBotonCentrado,
  MensajeExito,
  MensajeError,
  Select,
  LeyendaError,
} from "./elementos/Formularios";


const categorias = [
  {
    "pais" : "Colombia",
    "estados" : ["Amazonas", "Antioquia", "Arauca", "Atlántico", "Bogotá", "Bolívar", "Boyacá", "Caldas", "Caquetá", "Casanare", "Cauca", "Cesar", "Chóco", "Córdoba", "Cundinamarca", "Guainia", "Guaviare", "Huila", "La Guajira"],
  },
  {
    "pais" : "Canada",
    "estados" : ["Ciudad de Quebec", "Regina", "Calgary"]
  },
]

console.log("categorias", categorias)

const MenuDesplegable = () => {

  const [idArticulos, setIdArticulos] = useState(-1);

  // Funcion evento cambiar articulos
  const handlerCargarArticulo = function(e) {
    const opcion = e.target.value;
    // Validar carga de -1 y desde 0 seleccion articulo
    // console.log(opcion);
    // Cambio del idArticulo de acuerdo a la opción seleccionada
    setIdArticulos(opcion);
  }

  return (
    <>
      <div className="row">

      <div className="col-md-6">
                <Label htmlFor="tipoPersona">Tipo de Persona *</Label>
                <Select
                  required
                  id="tipoPersona"
                  data-toggle="tooltip"
                  title="Corresponde al tipo de persona, que efectúa el trámite, Natural o Juridica"
                  value={tipoPersona}
                  onChange={(e) => setTipoPersona(e.currentTarget.value)}
                >
                  <option value="" disabled selected hidden>
                    Ej. Natural
                  </option>
                  <option value="natural">Natural</option>
                  <option value="juridica">Juridica</option>
                </Select>
                <LeyendaError>Campo tipo persona es requerido</LeyendaError>
              </div>

        <div className="col-md-6">
          <h3>Categorías</h3>
          <select name="categorias" id="selCategorias" onClick={handlerCargarArticulo}>
            <option value={-1}> Ej. Colombia </option>
            {
              categorias.map((item, index) => (
                <option key={"categoria"+index} value={index}>
                  { item.pais }
                </option>
              ))
            }
          </select>
        </div>
        <div className="col-md-6">
          <h3>Articulos</h3>
          <select name="articulos" id="searticulo">
            <option> Ej. Cundinamarca </option>
            {
              idArticulos > -1 &&
              (
                categorias[idArticulos].estados.map((item, i) => (
                  <option key={"articulo"+i} value="">
                    {item}
                  </option>
                ))
              )
            }
          </select>
        </div>
      </div>
    </>
  );
}

export default MenuDesplegable;
