import React from 'react'
import { Input, Label, GrupoInput, LeyendaError } from "../elementos/Formularios"

export default function ComponenteInput(
    {estado, cambiarEstado, tipo, label, placeholder, name, leyendaError }) {


    const onChange = (e) => {
		cambiarEstado({...estado, campo: e.target.value});
	}

    const validacion = () => {
		if(expresionRegular){
			if(expresionRegular.test(estado.campo)){
				cambiarEstado({...estado, valido: 'true'})
			} else {
				cambiarEstado({...estado, valido: 'false'})
			}
		}
	}


    return (
        <div>
            <Label htmlFor={name} valido={estado.valido}>
                {label}
            </Label>
            <GrupoInput>
                <Input 
                    type={tipo} 
                    placeholder={placeholder} 
                    id={name} 
                    value={estado.campo}
                    onChange={onChange}
                    onKeyUp={validacion}
                    onBlur={validacion}
                    valido={estado.valido}
                />
            </GrupoInput>
            <LeyendaError valido={estado.valido}>{leyendaError}</LeyendaError>
        </div>
    )
}
