import React, { useRef, useState } from "react";
import ReCAPTCHA from "react-google-recaptcha";
import styled from "styled-components";
import "./SolicitudTramite.scss";
import { Link } from "react-router-dom";
import Modal from "../../Modal/Modal";

import { ContenedorBotonCentrado, MensajeExito, MensajeError } from "../../../Pages/FormularioSolicitud/elementos/Formularios";

import { faExclamationTriangle } from "@fortawesome/free-solid-svg-icons";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";



export default function SolicitudTramite() {
  const [estadoModal, cambiarEstadoModal] = useState(false);

  const [captchaValido, cambiarCaptchaValido] = useState(null);
  const [usuarioValido, cambiarUsuarioValido] = useState(false);

  const captcha = useRef(null);

  const onChange = () => {
    if (captcha.current.getValue()) {
      console.log("El usuario no es un robot");
      cambiarCaptchaValido(true);
    }
  };

  const submit = (e) => {
    e.preventDefault();

    //Colocar aquí la validación de los inputs del formulario
    //Si son correctos ya podemos enviar el formulario, actualizar la interfaz etc
    if (captcha.current.getValue()) {
      console.log("El usuario no es un robot");
    } else {
      console.log("Por favor acepta el captcha");
    }
  };

  return (
    <div className="col-12 col-sm-6 col-lg-4" onSubmit={submit}>
      <div className="tarjeta-acciones mb-4">
        <div className="head-acciones">
          <Modal
            estado={estadoModal}
            cambiarEstado={cambiarEstadoModal}
            titulo=""
            mostrarHeader={true}
            mostrarOverlay={true}
            posicionModal={"start"}
            padding={"20px"}
          >
            {
              !usuarioValido
            }



          </Modal>
          <h6
            className="titulo-card"
            onClick={() => cambiarEstadoModal(!estadoModal)}
          >
            Solicita el Trámite de Homologación
          </h6>
        </div>
        <div className="body-acciones">
          <p className="texto-card">
            Realice la solicitud del tramite ante la Comisión de Regulación de
            Comunicaciones.
          </p>
        </div>
      </div>
    </div>
  );
}

const Boton = styled.button`
  border-radius: 20px;
  border_radius: 100px;
  border: none;
  background: #1766dc;
  cursor: pointer;
  display: block;
  font: normal bold 15px/5px "Works Sans", sans-serif;
  padding: 10px 30px;
  transition: 0.3s ease all;

  a {
    color: #ffff;
  }

  &:hover {
    background: #004884;



  }
`;

const Boton2 = styled.button`
  border-radius: 20px;
  border: 2px solid #3366CC;
  background: #FFFF;
  color: #3366CC;
  cursor: pointer;
  display: block;
  font-family: "Roboto", sans-serif;
  font-weight: 500;
  padding: 10px 30px;
  transition: 0.3s ease all;

  &:hover {
    background: #0066ff;
    color: #FFFF;
  }
`;

const Contenido = styled.div`
  display: flex;
  padding: 20px;
  flex-direction: column;
  max-height: calc(100vh - 210px);
  overflow-y: auto;

  ::-webkit-scrollbar {
    -webkit-appearance: none;
}


  h1 {
    color: #13386D;
    font: normal 600 18px/10px "Montserrat", sans-serif;
    line-height: 1.5;
    margin-bottom: 10px;
  }

  h6 {
    color: #4B4B4B;
    font: normal normal 1rem/10px "Works Sans", sans-serif;
    margin: 0;
    text-align: left;
    line-height: 1.5;
  }

  a {
    font: normal normal 1rem/10px "Works Sans", sans-serif;
  }
`;

const ContenedorBotones = styled.div`
  padding: 40px;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  gap: 20px;
`;
