import React, {useState} from "react";

const categorias = [
  {
    "nombre" : "Audio y Video",
    "articulos" : ["Monitor", "Parlantes", "Televisor"]
  },
  {
    "nombre" : "Ferreteria",
    "articulos" : ["Martillo", "Taladro", "Cortadora"]
  },
]

console.log("categorias", categorias)

const MenuDesplegable = () => {

  const [idArticulos, setIdArticulos] = useState(-1);

  // Funcion evento cambiar articulos
  const handlerCargarArticulo = function(e) {
    const opcion = e.target.value;
    // Validar carga de -1 y desde 0 seleccion articulo
    // console.log(opcion);
    // Cambio del idArticulo de acuerdo a la opción seleccionada
    setIdArticulos(opcion);
  }

  return (
    <>
      <div className="row">
        <div className="col-md-6">
          <h3>Categorías</h3>
          <select name="categorias" id="selCategorias" onClick={handlerCargarArticulo}>
            <option value={-1}> Seleccione una opción: </option>
            {
              categorias.map((item, index) => (
                <option key={"categoria"+index} value={index}>
                  { item.nombre }
                </option>
              ))
            }
          </select>
        </div>
        <div className="col-md-6">
          <h3>Articulos</h3>
          <select name="articulos" id="searticulo">
            {
              idArticulos > -1 &&
              (
                categorias
              )
            }
          </select>
        </div>
      </div>
    </>
  );
}

export default MenuDesplegable;
