import React, { useState, useRef } from "react";
import View from "./View";

import Header from "../../components/Header/Header";
import Navegacion from "../../components/Navigation/Navigation";
import NavProceso from "../../components/NavProceso/NavProceso";
import BotonTutoriales from "../../components/Botones/BotonTutoriales/BotonTutoriales";
import BotonDeDudas from "../../components/Botones/BotonDeDudas/BotonDeDudas";
import CalificacionExperiencia from "../../components/Cards/CalificacionExperiencia/CalificacionExperiencia";
import BotonInicio from "../../components/Botones/BotonInicio/BotonInicio";
import Footer from "../../components/Footer/Footer";
import FooterGov from "../../components/FooterGov/FooterGov";

import { Boton } from "../FormularioSolicitud/elementos/Formularios";

const PruebaRespuesta = () => {

  // Campos del formulario a obtener valores
  const [formValues, setFormValues] = useState({
    name: "",
    priceUnitary: "",
    size: "",
    description: ""
  });

  //Cargar i
  const [isFormVisible, setIsFormVisible] = useState(true);
  // Uso ref para campo adjuntar archivo
  const inputFileRef = useRef();

  // Cambio de los valores por defecto de cada campo
  const handleChange = (event) => {
    const { name, value } = event.target;
    console.log(name, value);

    setFormValues({ ...formValues, [name]: value });
  };

  // Acciones al presionar boton de envío
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formValues);
    console.log(inputFileRef?.current?.files);
    setIsFormVisible(false);
  };

  return (
    <>
      <div className="container">
        <Header />

        <div className="row">

          <div className="col-md-8">
          <Navegacion
              inicio="Inicio"
              pagina="Solicitud de Homologación..."
              paginaActual="Formulario de Solicitud de Homologación..."
            />
            <NavProceso />

            <br/><br/>



      <div className="App">
        {isFormVisible ? (
          <form id="formu" onSubmit={handleSubmit} className="row">
            <h1>FORM SEND</h1>
            <div className="col-md-6">
              <label>Name</label>
              <input
                placeholder="Text input"
                name="name"
                value={formValues?.name}
                onChange={handleChange}
              />
            </div>
            <div className="col-md-6">
              <label>Size</label>
              <input
                type="number"
                placeholder="Text input"
                name="size"
                value={formValues.size}
                onChange={handleChange}
              />
            </div>
            <div className="col-md-6">
              <label>Price Unitary</label>
              <input
                type="number"
                placeholder="Text input"
                name="priceUnitary"
                value={formValues.priceUnitary}
                onChange={handleChange}
              />
            </div>
            <div className="col-md-6">
              <label>Description</label>
              <input
                placeholder="Text input"
                name="description"
                value={formValues.description}
                onChange={handleChange}
              />
            </div>
            <div className="col-md-6">
              <label>File / Image</label>
              <input type="file" ref={inputFileRef} />
            </div>
            <button type="submit" className="color-primary">
              Save
            </button>
          </form>
        ) : (
          <View data={formValues} />
        )}
      </div>


            <div>
              <Boton>
                VERIFICAR
              </Boton>
            </div>
          </div>

          <br/><br/>

          <div className="col-lg-1"></div>

          <div className="col-lg-3 p-0">
            <aside className="aside">
              <br/><br/>
              <BotonTutoriales />
              <BotonDeDudas />
              <br/><br/>
              <CalificacionExperiencia />
              <br/><br/>
              <BotonInicio />
            </aside>
          </div>
        </div>
      </div>
      <Footer />
      <FooterGov />
    </>
  );
};

export default PruebaRespuesta;
