const Boton2 = styled.button`
  border-radius: 20px;
  border: 2px solid #3366cc;
  background: #ffff;
  color: #3366cc;
  cursor: pointer;
  display: block;
  font-family: "Roboto", sans-serif;
  font-weight: 500;
  padding: 10px 30px;
  transition: 0.3s ease all;

