                          xmlns="http://www.w3.org/2000/svg"
                          width="16"
                          height="16"
                          fill="currentColor"
                          class="bi bi-trash"
                          viewBox="0 0 16 16"
                        >
                          <path d="M5.5 5.5A.5.5 0 0 1 6 6v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5zm2.5 0a.5.5 0 0 1 .5.5v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5zm3 .5a.5.5 0 0 0-1 0v6a.5.5 0 0 0 1 0V6z" />
                          <path
                            fill-rule="evenodd"
                            d="M14.5 3a1 1 0 0 1-1 1H13v9a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V4h-.5a1 1 0 0 1-1-1V2a1 1 0 0 1 1-1H6a1 1 0 0 1 1-1h2a1 1 0 0 1 1 1h3.5a1 1 0 0 1 1 1v1zM4.118 4 4 4.059V13a1 1 0 0 0 1 1h6a1 1 0 0 0 1-1V4.059L11.882 4H4.118zM2.5 3V2h11v1h-11z"
                          />
                        </svg>
                      </td>
                    </tr>
                  </tbody>
                </table>
              </div>
            </div>
            <br />
            <Contenido>
              <ContenedorBotonCentrado>
                <Label>
                  <input
                    type="checkbox"
                    name="terminos"
                    id="terminos"
                    checked={terminos}
                    onChange={onChangeTerminos}
                  />
                  Acepto que he leido los Términos y condiciones *
                </Label>
                <a
                  href="https://www.crcom.gov.co/uploads/images/files/Politica%20de%20Tratamiento%20Info%20Personal.pdf"
                  target="_blank"
                >
                  Ver términos y condiciones
                </a>
              </ContenedorBotonCentrado>
              <ContenedorBotonCentrado>
                <Label>
                  <input
                    type="checkbox"
                    name="tratamiento"
                    id="tratamiento"
                    checked={tratamiento}
                    onChange={onChangeTratamiento}
                  />
                  Autorizo el tratamiento de datos personales
                </Label>
                <a
                  href="https://crcom.gov.co/sites/default/files/transparencia/contenido_multimedia/Politica_de_tratamiento_info_personal.pdf"
                  target="_blank"
                >
                  Ver política tratamiento de datos personales
                </a>
              </ContenedorBotonCentrado>
              <br />
              <br />
              <br />

              <ContenedorBotonCentrado className="recaptcha">
                <ReCAPTCHA
                  ref={captcha}
                  sitekey="6Lc9VDIeAAAAAHHQA1wEjx1FKlTy9uWIrZKGwwvN"
                  onChange={onChange}
                />
              </ContenedorBotonCentrado>

              {captchaValido === false && (
                <div className="error-captcha">Por favor acepta el captcha</div>
              )}
              <ContenedorBotones>
                <Boton type="submit">
                  <Link to="/SolicitudHomologacion" className="irTramite">
                    REGISTRAR
                  </Link>
                </Boton>
                <br />
                <Boton2>CANCELAR</Boton2>
              </ContenedorBotones>
            </Contenido>
            <br />
            <br />
            <br />
            {formularioValido === false && (
              <MensajeError>
                <p>
                  <FontAwesomeIcon icon={faExclamationTriangle} />
                  <b>Error: </b> Por favor rellena el formulario correctamente.
                </p>
              </MensajeError>
            )}
            <ContenedorBotonCentrado>
              {formularioValido === true && (
                <MensajeExito id="msjexito">
                  <p>
                    <FontAwesomeIcon icon={faCheckCircle} />
                    <b>Exitosa: </b>Su solicitud se ha registrado
                    satisfactoriamente
                  </p>
                </MensajeExito>
              )}
            </ContenedorBotonCentrado>
          </div>

          <div className="col-lg-1"></div>
          <div className="col-lg-3 p-0">
            <aside className="aside">
              <br />
              <br />
              <BotonTutoriales />
              <BotonDeDudas />
              <br />
              <br />
              <CalificacionExperiencia />
              <br />
              <br />
              <BotonInicio />
            </aside>
          </div>
        </div>
      </div>
      <Footer />
      <FooterGov />
    </>
  );
};

