import styled from 'styled-components';

const colores = {
    borde: "#4B4B4B",
    error: "#A80521",
    exito: "#3366CC"
}

const Formulario = styled.form `
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 20px;

    @media (max-width: 800px) {
        grid-template-columns: 1fr;
    }
`;

const Label = styled.label `
    display: block;
    font-weight: 700;
    padding: 10px;
    min-height: 40px;
    cursor: pointer;
`;

export { Formulario, Label };
