import React, { useState } from "react";
import styled from "styled-components";

function DragArea() {

  const [seleccionArchivo, setSeleccionArchivo] = useState(null);
  const [seleccionArchivo2, setSeleccionArchivo2] = useState(null);

  const onFileChange = (e) => {
    //console.log(e.target.files);
    const file = e.target.files[0]

    if (e.target.files[0] !== undefined) {
      const reader = new FileReader();
      reader.readAsDataURL(e.target.files[0]);

      reader.onload = (e) => {
        e.preventDefault();
        console.log(e)
        setSeleccionArchivo(e.target.result); // le damos el binario de la imagen para mostrarla en pantalla
      };
    }
  };

  const onFileChange2 = (e) => {
    //console.log(e.target.files);
    const file = e.target.files[0]

    if (e.target.files[0] !== undefined) {
      const reader = new FileReader();
      reader.readAsDataURL(e.target.files[0]);

      reader.onload = (e) => {
        e.preventDefault();
        console.log(e)
        setSeleccionArchivo2(e.target.result); // le damos el binario de la imagen para mostrarla en pantalla
      };
    }
  };

  return (
    <div>
      <StyleDragArea>
        <br />
        <h6 className="subtitle-form"> Etiqueta del Equipo *</h6>
        <div className="image-upload-wrap">
          <input
            className="file-upload-input"
            type="file"
            accept="image/*, application/pdf,application/vnd.ms-excel"
            multiple
            onChange={(e) => {
              onFileChange(e);
            }}
          />
          <div className="text-information">
            <svg xmlns="http://www.w3.org/2000/svg" width="50" height="35" fill="currentColor" class="bi bi-paperclip" viewBox="0 0 16 16">
              <path d="M4.5 3a2.5 2.5 0 0 1 5 0v9a1.5 1.5 0 0 1-3 0V5a.5.5 0 0 1 1 0v7a.5.5 0 0 0 1 0V3a1.5 1.5 0 1 0-3 0v9a2.5 2.5 0 0 0 5 0V5a.5.5 0 0 1 1 0v7a3.5 3.5 0 1 1-7 0V3z"/>
            </svg>
            <h3>Arrastre aquí su archivo o haga click para añadir.</h3>
          </div>
        </div>
        <br />
        <h6 className="subtitle-form"> Certificado de conformidad de normas técnicas</h6>
        <div className="image-upload-wrap2">
          <input
            className="file-upload-input"
            type="file"
            accept="image/*, application/pdf,application/vnd.ms-excel"
            multiple
            onChange={(e) => {
              onFileChange(e);
            }}
          />
          <div className="text-information">
            <svg xmlns="http://www.w3.org/2000/svg" width="50" height="35" fill="currentColor" class="bi bi-paperclip" viewBox="0 0 16 16">
              <path d="M4.5 3a2.5 2.5 0 0 1 5 0v9a1.5 1.5 0 0 1-3 0V5a.5.5 0 0 1 1 0v7a.5.5 0 0 0 1 0V3a1.5 1.5 0 1 0-3 0v9a2.5 2.5 0 0 0 5 0V5a.5.5 0 0 1 1 0v7a3.5 3.5 0 1 1-7 0V3z"/>
            </svg>
            <h3>Arrastre aquí su archivo o haga click para añadir.</h3>
          </div>
        </div>


      </StyleDragArea>
      <br/>
      <br/>
      <table class="table">
                  <thead>
                    <tr>
                      <th scope="col" class="titulo-tableta">
                        Nombre del Archivo
                      </th>
                      <th scope="col" class="titulo-tableta">
                        Archivo Cargado

                      </th>
                      <th scope="col" class="titulo-tableta">
                        Eliminar Archivo
                      </th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <th scope="row">Etiqueta del equipo *</th>
                      <td>
                      <img
                        src={seleccionArchivo}
                        alt=""
                        height="20px"
                        width="70px"
                      />
                      </td>
                      <td>
                        <svg
                          xmlns="http://www.w3.org/2000/svg"
                          width="16"
                          height="16"
                          fill="currentColor"
                          class="bi bi-trash"
                          viewBox="0 0 16 16"
                        >
                          <path d="M5.5 5.5A.5.5 0 0 1 6 6v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5zm2.5 0a.5.5 0 0 1 .5.5v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5zm3 .5a.5.5 0 0 0-1 0v6a.5.5 0 0 0 1 0V6z" />
                          <path
                            fill-rule="evenodd"
                            d="M14.5 3a1 1 0 0 1-1 1H13v9a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V4h-.5a1 1 0 0 1-1-1V2a1 1 0 0 1 1-1H6a1 1 0 0 1 1-1h2a1 1 0 0 1 1 1h3.5a1 1 0 0 1 1 1v1zM4.118 4 4 4.059V13a1 1 0 0 0 1 1h6a1 1 0 0 0 1-1V4.059L11.882 4H4.118zM2.5 3V2h11v1h-11z"
                          />
                        </svg>
                      </td>
                    </tr>
                    <tr>
                      <th scope="row">
                        Certificado de conformidad de normas técnicas (opcional)
                      </th>
                      <td>
                      .......
                      </td>
                      <td>
                        <svg
                          xmlns="http://www.w3.org/2000/svg"
                          width="16"
                          height="16"
                          fill="currentColor"
                          class="bi bi-trash"
                          viewBox="0 0 16 16"
                        >
                          <path d="M5.5 5.5A.5.5 0 0 1 6 6v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5zm2.5 0a.5.5 0 0 1 .5.5v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5zm3 .5a.5.5 0 0 0-1 0v6a.5.5 0 0 0 1 0V6z" />
                          <path
                            fill-rule="evenodd"
                            d="M14.5 3a1 1 0 0 1-1 1H13v9a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V4h-.5a1 1 0 0 1-1-1V2a1 1 0 0 1 1-1H6a1 1 0 0 1 1-1h2a1 1 0 0 1 1 1h3.5a1 1 0 0 1 1 1v1zM4.118 4 4 4.059V13a1 1 0 0 0 1 1h6a1 1 0 0 0 1-1V4.059L11.882 4H4.118zM2.5 3V2h11v1h-11z"
                          />
                        </svg>
                      </td>
                    </tr>
                  </tbody>
                </table>
      <div className="center">

        </div>
    </div>
  );
}

export default DragArea;

const StyleDragArea = styled.div`
  .center {
    display: flex;
    justify-content: center;
    align-items: center;
  }
  .file-upload-content {
    display: none;
    text-align: center;

  }
  .file-upload-input {
    position: absolute;
    margin: 0;
    padding: 0;
    width: 100%;
    height: 100%;
    outline: none;
    opacity: 0;
    cursor: pointer;
  }
  .image-upload-wrap {
    position: relative;
    height: 100px;
    border: 2px dashed #3366CC;
    margin: 0px;

    h3 {
      font: normal bold 1rem/30px "Work Sans", san serif;
      color: #3366CC;
    }

    svg {
      fill: #3366CC;
      float: left;
    }
  }
  .image-upload-wrap:hover {
    background-color: #3366CC;
    border: 2px solid #3366CC;

    h3 {
      color: #FFFF;
    }

    svg {
      fill: #FFFF;
    }
  }
  .text-information {
    margin-top: 30px;
    text-align: left;
  }

  .image-upload-wrap2 {
    position: relative;
    height: 100px;
    border: 2px dashed #3366CC;
    margin: 0px;

    h3 {
      font: normal bold 1rem/30px "Work Sans", san serif;
      color: #3366CC;
    }

