import React, { useState } from "react";
import styled from "styled-components";
import "./SolicitudTramite.scss";
import Modal from "../../Modal/Modal";

export default function SolicitudTramite() {

    const [estadoModal, cambiarEstadoModal] = useState(false);

    return (
        <div className="col-12 col-sm-6 col-lg-4">
        <div className="tarjeta-acciones mb-4">
          <div className="head-acciones">
          <Modal
                    estado={estadoModal}
                    cambiarEstado={cambiarEstadoModal}
                    titulo="Hola"
                    mostrarHeader={true}
                    mostrarOverlay={true}
                    posicionModal={'center'}
                    padding={'20px'}
                  >
                    <Contenido>
                      <h1>Ventana Modal</h1>
                      <p>Reutilizable y con opciones de personalización.</p>
                    <Boton onClick={() => cambiarEstadoModal(!estadoModal)}>
                      Aceptar
                    </Boton>
                    <br/>
                    <Boton onClick={() => cambiarEstadoModal(!estadoModal)}>
                      Rechazar
                    </Boton>
                    </Contenido>
                  </Modal>
                <h6 className='titulo-card' onClick={() => cambiarEstadoModal(!estadoModal)}>
                    Solicita el Trámite de Homologación
                </h6>
                <ContenedorBotones>
                    <Boton onClick={() => cambiarEstadoModal(!estadoModal)}>Modal 1</Boton>

                  </ContenedorBotones>  
          </div>
          <div className="body-acciones">
            <p className="texto-card">
              Realice la solicitud del tramite ante la Comisión de Regulación de Comunicaciones.
            </p>
          </div>
        </div>
      </div>
    )
}

const Boton = styled.button`
  border-radius: 20px;
  border_radius: 100px;
  border: none;
  background: #1766DC;
  color: #ffff;
  cursor: pointer;
  display: block;
  font-family: 'Roboto', sans-serif;
  font-weight: 500;
  padding: 10px 30px;
  transition: .3s ease all;

  &:hover {
    background: #0066FF;
  }
`

const Contenido = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;

  h1 {
    font-size: 42px;
    font-weight: 700;
    margin-bottom: 10px; 
  }

  p {
    font-size: 18px;
    margin-bottom: 20px;
  }

  img {
    width: 100%;
    vertical-align: top;
    border-radius: 3px;
  }
`
