import React from "react";
import Header from "./components/Header/Header";
import "./assets/styles/App.scss";
import Footer from "./components/Footer/Footer";
import Section from "./components/Section/Section";
import Navbar from "./components/Navbar/Navbar";
import FooterGov from "./components/FooterGov/FooterGov";
import { BrowserRouter as Router, Routes, Route, Link, NavLink } from "react-router-dom";


const App = () => {
  return (
    <>

    <Router> {/* Para utilizar router todo se encierra en este */}
      <div className="container">

        <NavLink to="/otros" className="btn btn-warning" activeClassName="active">
          Otro con NavLink, resalta sirve para resaltar en la ruta active que se encuentre
        </NavLink>
        <Link to="/otrosO" className="btn btn-warning">
          Otro
        </Link>



        <nav className="btn-group">
          <ul>
            <li>
              <Link to="/" className="btn btn-warning">Home</Link>
            </li>
            <li>
              <Link to="/about" className="btn btn-danger">About</Link>
            </li>
            <li>
              <Link to="/users" className="btn btn-dark" activeClassName="active">Users</Link>
            </li>
          </ul>
        </nav>

        <Routes>  {/* Para mostrar el contenido dinamico de la ruta */}
          <Route exact path="/about" element={<About />} /> {/* Llamar el comonente o contenido dinamico y el path da la ruta donde esta */}
          <Route exact path="/users" element={<Users />} />
          <Route exact path="/" element={<Home />} />
        </Routes>
      </div>
    </Router>


    <div className="App">
      <Navbar />
      <div className="container">
        <Header />
        <Section />
      </div>
      <Footer />
      <FooterGov />
    </div>
    </>
  );
}

export default App;
