import React, { useRef, useState } from "react";
import "./Upload.scss";

export default function Dragdrop() {
  const [files, setFiles] = useState(null);
  const inputRef = useRef();

  const handleClick = () => {
    inputRef.current.click();
  };

  const removeFile = (name) => {
    const newFiles = files.filter((file) => file.name !== name);
    setFiles(newFiles);
  };

  const preventBubbling = (e) => {
    e.stopPropagation();
    e.preventDefault();
  };

  return (
    <div className="Upload" css={CSS}>
      <div className="inner">
        <div className="list">
          <h5>Your Files:</h5>

          {files && (
            <ul className="files">
              {files.map((file, i) => (
                <li key={file.name}>
                  {i + 1}. {file.name}
                  <span onClick={() => removeFile(file.name)}>
                    <i className="fa fa-times">X</i>
                  </span>
                </li>
              ))}
            </ul>
          )}
        </div>

        <div
          className="form"
          onDragEnter={preventBubbling}
          onDragOver={preventBubbling}
          onDrop={(e) => {
            preventBubbling(e);
            setFiles(Array.from(e.dataTransfer.files));
          }}
        >
          <svg
              xmlns="http://www.w3.org/2000/svg"
              width="50"
              height="35"
              fill="currentColor"
              className="bi bi-paperclip"
              viewBox="0 0 16 16"
            >
              <path d="M4.5 3a2.5 2.5 0 0 1 5 0v9a1.5 1.5 0 0 1-3 0V5a.5.5 0 0 1 1 0v7a.5.5 0 0 0 1 0V3a1.5 1.5 0 1 0-3 0v9a2.5 2.5 0 0 0 5 0V5a.5.5 0 0 1 1 0v7a3.5 3.5 0 1 1-7 0V3z" />
            </svg>
            <input
            ref={inputRef}
            type="file"
            multiple
            style={{ display: "none" }}
            onChange={(e) => setFiles(Array.from(e.target.files))}
          >
          <p onClick={handleClick}>Arrastre aquí su archivo o haga click para añadir.</p>
          </input>


        </div>
      </div>
    </div>
  );
}
