import React from 'react'
import { Input, Label, GrupoInput, LeyendaError } from "../elementos/Formularios"

export default function ComponenteInput({estado, cambiarEstado, tipo, label, placeholder, name, leyendaError, expresionRegular, funcion}) {

    const onChange = (e) => {
		cambiarEstado({...estado, campo: e.target.value});
	}

    return (
        <div>
            <Label htmlFor={name}>{label}</Label>
            <GrupoInput>
                <Input 
                    type={tipo} 
                    placeholder={placeholder} 
                    id={name} 
                    value={estado.campo}
                    onChange={onChange}
                />
            </GrupoInput>
            <LeyendaError>{leyendaError}</LeyendaError>
        </div>
    )
}
