import React, { useState } from "react";

import {
  Formulario,
  Label,
  Input,
  GrupoInput,
  LeyendaError,
} from "../FormularioSolicitud/elementos/Formularios";

const RtaTramite = () => {

  return (
    <>
      <FormularioNormal />
      <FormularioSimplificado />
      <Notas />
      <FormularioPrueba />
    </>
  );
};

export default RtaTramite;

//FORMA NORMAL OBTENER DATOS FORMULARIOS

const FormularioNormal = () => {
  const [nombre, setNombre] = useState("");
  const [sabor, setSabor] = useState("");
  const [lenguaje, setLenguaje] = useState("");
  const [terminos, setTerminos] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    alert("El formulario se ha enviado");
  };

  return (
    <main className="container">
      <div className="row">
        <div className="col-md-6">
          <h1>Formulario Normal</h1>
          <Formulario onSubmit={handleSubmit}>
            <GrupoInput>
              <Label htmlFor="nombre">Nombre</Label>
              <Input
                type="text"
                id="nombre"
                name="nombre"
                placeholder="Ej. paco"
                value={nombre}
                onChange={(e) => setNombre(e.target.value)} //Elemento input y textarea
              />
            </GrupoInput>
            <LeyendaError>
              Este campo es requerido, y solo acepta números
            </LeyendaError>

            <br />

            <p>Elige tu sabor JS favorito</p>
            <br />
            <input
              type="radio"
              id="vanilla"
              name="sabor"
              value="vanilla"
              onChange={(e) => setSabor(e.target.value)}
              defaultChecked
            />
            <Label htmlFor="vanilla">Vanilla</Label>
            <input
              type="radio"
              id="react"
              name="sabor"
              value="react"
              onChange={(e) => setSabor(e.target.value)}
            />
            <Label htmlFor="react">React</Label>
            <input
              type="radio"
              id="angular"
              name="sabor"
              value="angular"
              onChange={(e) => setSabor(e.target.value)}
            />
            <Label htmlFor="angular">Angular</Label>
            <input
              type="radio"
              id="vue"
              name="sabor"
              value="vue"
              onChange={(e) => setSabor(e.target.value)}
            />
            <Label htmlFor="vue">Vue</Label>

            <br />

            <GrupoInput>
            <p>Elige tu lenguaje de programación favorito</p>

            <select
              name="lenguaje"
              onChange={(e) => setLenguaje(e.target.value)}
              defaultValue=""
            >
              <option value="">---</option>
              <option value="js">JavaScript</option>
              <option value="py">Python</option>
              <option value="java">Java</option>
              <option value="c">C++</option>
              <option value="php">PHP</option>
              <option value="rust">Rust</option>
              <option value="go">Go</option>
              <option value="sw">Swift</option>
            </select>
            </GrupoInput>

            <br />

            <Label htmlFor="terminos">Acepto términos y condiciones</Label>
            <Input
              type="checkbox"
              id="terminos"
              name="terminos"
              onChange={(e) => setTerminos(e.target.checked)}
            />

            <br />

            <input type="submit" />
          </Formulario>
        </div>
      </div>
    </main>
  );
};


//FORMA SIMPLIFICADA OBTENER DATOS FORMULARIOS

const FormularioSimplificado = () => {
  //Solo tendremos una variable de estado de objeto vacia
  const [form, setForm] = useState({});

  //Asignar a los elementos seleccionados el evento, para campos values
  const handleChange = e => {
    //Invocamos funcion de actualizacion
    setForm({
      ...form,  //El objeto que trae el formulario, se mezcla con lo que ingresa
      [e.target.name]:e.target.value, //Actualizar los campos con nuevo valor
    })
  }

  //Asignar a los elementos seleccionados el evento, para campos checkend
  const handleChecked = e => {
    //Invocamos funcion de actualizacion
    setForm({
      ...form,  
      [e.target.name]:e.target.checked, //Actualizar los campos con nuevo valor boolean
    })
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    alert("El formulario se ha enviado");
  };

  return (
    <main className="container">
      <div className="row">
        <div className="col-md-6">
          <h1>Formulario Simplificado</h1>
          <Formulario onSubmit={handleSubmit}>
            <GrupoInput>
              <Label htmlFor="nombre">Nombre</Label>
              <Input
                type="text"
                id="nombre"
                name="nombre"
                placeholder="Ej. paco"
                value={form.nombre} //Coloar form.nombre ya que se vincula la variable, y los valores ahora estan en un objeto, aplicar a cada caja de texto si se tiene
                onChange={handleChange} //Elemento input y textarea
              />
            </GrupoInput>
            <LeyendaError>
              Este campo es requerido, y solo acepta números
            </LeyendaError>

            <br />

            <p>Elige tu sabor JS favorito</p>
            <br />
            <input
              type="radio"
              id="vanilla"
              name="sabor"
              value="vanilla"
              onChange={handleChange}
              defaultChecked
            />
            <Label htmlFor="vanilla">Vanilla</Label>
            <input
              type="radio"
              id="react"
              name="sabor"
              value="react"
              onChange={handleChange}
            />
            <Label htmlFor="react">React</Label>
            <input
              type="radio"
              id="angular"
              name="sabor"
              value="angular"
              onChange={handleChange}
            />
            <Label htmlFor="angular">Angular</Label>
            <input
              type="radio"
              id="vue"
              name="sabor"
              value="vue"
              onChange={handleChange}
            />
            <Label htmlFor="vue">Vue</Label>

            <br />

            <GrupoInput>
            <p>Elige tu lenguaje de programación favorito</p>

            <select
              name="lenguaje"
              onChange={handleChange}
              defaultValue=""
            >
              <option value="">---</option>
              <option value="js">JavaScript</option>
              <option value="py">Python</option>
              <option value="java">Java</option>
              <option value="c">C++</option>
              <option value="php">PHP</option>
              <option value="rust">Rust</option>
              <option value="go">Go</option>
              <option value="sw">Swift</option>
            </select>
            </GrupoInput>

            <br />

            <Label htmlFor="terminos">Acepto términos y condiciones</Label>
            <Input
              type="checkbox"
              id="terminos"
              name="terminos"
              onChange={handleChecked}
            />

            <br />

            <input type="submit" />
          </Formulario>
        </div>
      </div>
    </main>
  );
};


// PRUEBA PARA AGREGAR TAC

const Notas = () => {
  const [newNote, setNewNote] = useState('');

  const handleChange = (event) => {
    setNewNote(event.target.value)
  }

  const handleClick = (event) => {
    console.log('crear nota')
    console.log(newNote)
  }

  return (
    <>
      <div className="col-md-6">
        <h1>Notes</h1>
      </div>
      <div>
        <input type='text' onChange={handleChange} value={newNote} />
        <button onClick={handleClick}>Agregar TAC</button>
      </div>
      <ul>
        <li>{newNote}</li>
        <li></li>
      </ul>
    </>
  );
};

// PRUEBA FORMULARIO ENVIO DATOS 

const FormularioPrueba = () => {
  const [formValues, setFormValues] = useState({
    name: '',
    priceUnitary: '',
    size: '',
    description: '',
  });

  const handleChange = (event) => {
    const {name, value} = event.target
    console.log(name, value)

    setFormValues({...formValues, [name]: value})
  }


  return (
    <>
      <div className="col-md-6">
      <h1>FORMULARIO PRUEBA</h1>
        <label>Name</label>
        <input 
        placeholder="Text input"
        name="name"
        value={formValues.name}
        onChange={handleChange}
        />
        <label>Size</label>
        <input 
        placeholder="Text input"
        name="size"
        value={formValues.size}
        onChange={handleChange}
        />
        <label>Price Unitary</label>
        <input 
        placeholder="Text input"
        name="priceUnitary"
        value={formValues.priceUnitary}
        onChange={handleChange}
        />
      </div>

    </>
  );
};
