import React from "react";
import { useForm } from "../../hooks/useForm";
import "./ContactForm.scss"

const initialForm = {
    name: "",
    email: "",
    subject: "",
    comments: ""
};

const validationsForm = (form) => {

};

const ContactForm = () => {
  const {
    form,
    errors,
    loading,
    response,
    handleChange,
    handleBlur,
    handleSubmit,
  } = useForm(initialForm, validationsForm);

  return (
    <div className="container">
    <div className="col-md-8">
      <h3 className="title-form">
        Formulario de Solicitud de Homologación de Equipos Terminales Móviles
      </h3>
      <br />
      <br />
      <form onSubmit={handleSubmit} className="row">

        <div className="row">
          <div className="col-6">
            <input
              type="text"
              name="name"
              placeholder="Ej: Pedro"
              onBlur={handleBlur}
              onChange={handleChange}
              value={form.name}
              required
            />
          </div>
          <div className="col-6">
            <input
              type="text"
              name="lastname"
              placeholder="Ej: Rodriguez"
              onBlur={handleBlur}
              onChange={handleChange}
              value={form.lastname}
              required
            />
          </div>
        </div><br/>
        <div className="row">
          <div className="col-md-6">
            <input
              type="email"
              name="email"
              placeholder="Ej: pedro.rodriguez@gmail.com"
              onBlur={handleBlur}
              onChange={handleChange}
              value={form.email}
              required
            />
          </div>
          <div className="col-md-6">
            <input
              type="text"
              name="subject"
              placeholder="Ej: El presenta asunto para tratar..."
              onBlur={handleBlur}
              onChange={handleChange}
              value={form.subject}
              required
            />
          </div>
        </div>
        <br/>
        <div className="row">
          <div className="col">
            <textarea
              type="text-area"
              name="comments"
              cols="50"
              rows="5"
              placeholder="Ej: Escribe tus comentarios..."
              onBlur={handleBlur}
              onChange={handleChange}
              value={form.comments}
              required
            >
            </textarea>
          </div>
        </div>
        <div className="box">
            <input type="submit" value="Enviar" />
        </div>

      </form>
      <br/><br/><br/><br/><br/><br/>
    </div>
    </div>
  );
};

export default ContactForm;
